        function readMore() {
            let maxCityChars = 1900;
            let maxChronicleChars = 550;
            let $cityContent = $('.city-content');
            let $chronicleContent = $('.chronicle-content');
            let cityContentText = $cityContent.text();
            let chronicleContentText = $chronicleContent.text();

            if (cityContentText.length > maxCityChars) {
                let cityTrimmedContent = cityContentText.substring(0, maxCityChars);
                $cityContent.text(cityTrimmedContent + '...');

                $('.city-read-more-btn').show();
            
                $(document).on('click', '.city-read-more-btn', function(e) {
                    e.preventDefault();
                    $cityContent.text(cityContentText);
                    $(this).hide();
                });
            } else {
                $('.city-read-more-btn').hide();
            }

            if (chronicleContentText.length > maxChronicleChars) {
                let chronicleTrimmedContent = chronicleContentText.substring(0, maxChronicleChars);
                $chronicleContent.text(chronicleTrimmedContent + '...');

                $('.chronicle-read-more-btn').show();
            
                $(document).on('click', '.chronicle-read-more-btn', function(e) {
                    e.preventDefault();
                    $chronicleContent.text(chronicleContentText);
                    $(this).hide();
                });
            } else {
                $('.chronicle-read-more-btn').hide();
            }
