            map = $('#palestine').vectorMap({
                map: 'palestine',
    			zoomButtons: false,
                zoomOnScroll: false,
                zoomOnScrollWheel: false,
                backgroundColor: 'transparent',
                regionStyle: {
                    initial: {
                        fill: '#474747',
                        'fill-opacity': 1,
                        stroke: '#ffffff',
                        'stroke-width': 1.7,
                        'stroke-opacity': 1
                    },
                    hover: {
                        'fill-opacity': 0.8,
                        cursor: 'pointer'
                    }
                },
                markers: citiesMarkersData,
               
                markerStyle: {
                    initial: {
                        fill: '#FFF',
                        stroke: '#FFF',
                        'stroke-width': 1,
                        r: 4
                    },
                    hover: {
                        fill: '#FFF',
                        stroke: '#FFF',
                        'stroke-width': 1,
                        r: 4
                    }
                },
                onRegionTipShow: function (e, el, code) {
                    el.html(el.html());
                },
                onRegionClick: function(event, code) {
                    let map = $('#palestine').vectorMap('get', 'mapObject');

                    if (Math.ceil(map.scale) !== 1) {
                        locationsMarkersData = [];
                        map.removeAllMarkers();
                        map.addMarkers(citiesMarkersData);
                        map.setFocus({scale: 1, x: 0.5, y: 0.5, animate: true});
                        $('.zoom-out').fadeOut();
                    } else {
                        $.ajax({
                            url: '{{ url('/get-markers/') }}' + '/' + code,
                            type: 'GET',
                            dataType: 'json',
                            success: function (response) {
                                locationsMarkersData = JSON.parse(response.locationsMarkersData);
                                let cityId = $('#city-description').data('city_id');
                                if(cityId != response.cityId) {
                                    $('#city-description').html(response.city);
                                    $('#city-description').data('city_id', response.cityId)
                                    $('#chronicle-box').html('');
                                }
                                readMore();
                                if (locationsMarkersData.length > 0) {
                                    map.removeAllMarkers();
                                    map.addMarkers(locationsMarkersData);
                                    map.setFocus({scale: 1, region: code, animate: true });
                                    $('.zoom-out').fadeIn();
                                    $('.jvectormap-marker').css({
                                        'r': 12
                                    });
                                }
                            },
                            error: function (error) {
                                console.error(error);
                            }
                        });
                    }
                },
                onMarkerClick: function (event, index) {

                    let clickedMarker = null;
                    if (locationsMarkersData[index]?.locId) {
                        clickedMarker = locationsMarkersData[index].locId;
                        $.ajax({
                            url: '{{ url('/get-chronicle-by-marker/') }}' + '/' + clickedMarker,
                            type: 'GET',
                            dataType: 'html',
                            beforeSend: function() {
                                $('html, body').animate({
                                    scrollTop: $("#chronicle-box").offset().top - 100
                                }, 1000); 
                                $('#chronicle-box').html(loader);
                            },
                            success: function (response) {
                                $('#chronicle-box').html(response);
                                readMore();
                            },
                            error: function (error) {
                                console.error(error);
                            }
                        });
                    } else if (citiesMarkersData[index]?.id.startsWith("PS")) {
                        clickedMarker = citiesMarkersData[index].id;
                        let map = $('#palestine').vectorMap('get', 'mapObject');

                        $.ajax({
                            url: '{{ url('/get-markers/') }}' + '/' + clickedMarker,
                            type: 'GET',
                            dataType: 'json',
                            success: function (response) {
                                locationsMarkersData = JSON.parse(response.locationsMarkersData);
                                let cityId = $('#city-description').data('city_id');
                                if(cityId != response.cityId) {
                                    $('#city-description').html(response.city);
                                    $('#city-description').data('city_id', response.cityId)
                                    $('#chronicle-box').html('');
                                }
                                readMore();
                                if (locationsMarkersData.length > 0) {
                                    map.removeAllMarkers();
                                    map.addMarkers(locationsMarkersData);
                                    map.setFocus({scale: 1, region: clickedMarker, animate: true });
                                    $('.zoom-out').fadeIn();
                                    $('.jvectormap-marker').css({
                                        'r': 12
                                    });
                                }
                            },
                            error: function (error) {
                                console.error(error);
                            }
                        });
                    }
                },
            });

            $(document).on('click', '.zoom-out', function(e) {
                e.preventDefault();
                let map = $('#palestine').vectorMap('get', 'mapObject');
                locationsMarkersData = [];
                map.removeAllMarkers();
                map.addMarkers(citiesMarkersData);
                map.setFocus({scale: 1, x: 0.5, y: 0.5, animate: true});
                $('.zoom-out').fadeOut();
            });
        });

function showContainers() {
    const districtContainer = document.getElementById('district-container');
    const videoContainer = document.getElementById('video-container');
    const mapDiv = document.getElementById('map-div');
    const exploreDiv = document.getElementById('explore-div');

    districtContainer.style.display = 'block';
    videoContainer.style.display = 'block';
    exploreDiv.style.display = 'none';

    districtContainer.classList.remove('col-sm-4');
    districtContainer.classList.add('col-sm-4');

    videoContainer.classList.remove('col-sm-4');
    videoContainer.classList.add('col-sm-4');

    mapDiv.classList.remove('col-sm-4');
    mapDiv.classList.add('col-sm-4');
}

$('#palestine').on('click', 'path', showContainers);
$('#palestine').on('click', '.jvectormap-marker', showContainers);

        

    </script>
